package day17java;

import java.util.LinkedList;

public class CollectionsDemo3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
					LinkedList <Integer> oj = new LinkedList<>();
				    oj.add(1000);
				    oj.add(2000);
				    oj.add(3000);
				    oj.add(4000);
				    oj.add(5000);
				    oj.add(6000);
				    oj.add(7000);
				    System.out.println(oj);
				    oj.remove();
				    System.out.println(oj);
				    oj.peek();
				    System.out.println(oj);
				    oj.set(0,10000);
				    oj.set(4,10000);
				    System.out.println(oj);
				
				}

			}

